import React from 'react';
import style from 'PVWStyle/ReactWidgets/LayoutsWidget.mcss';

export default function threeByTwo(props) {
  return (
    <table className={props.active === '3x2' ? style.activeTable : style.table} name="3x2" onClick={props.onClick}>
      <tbody>
        <tr>
          <td className={props.activeRegion === 0 ? style.activeTd : style.td} />
          <td className={props.activeRegion === 1 ? style.activeTd : style.td} />
          <td className={props.activeRegion === 2 ? style.activeTd : style.td} />          
        </tr>
        <tr>
          <td className={props.activeRegion === 3 ? style.activeTd : style.td} />
          <td className={props.activeRegion === 4 ? style.activeTd : style.td} />
          <td className={props.activeRegion === 5 ? style.activeTd : style.td} />          
        </tr>
      </tbody>
    </table>);
}

threeByTwo.propTypes = {
  onClick: React.PropTypes.func,
  active: React.PropTypes.string,
  activeRegion: React.PropTypes.number,
};

threeByTwo.defaultProps = {
  onClick: () => {},
  activeRegion: -1,
};

