
export default function Histogram(chartState, csvReader, arraysInfo) {
  if (!chartState.x) {
    chartState.x = Object.keys(arraysInfo)[0];
  }
  return {
    forceNewPlot: true, // Due to a bug in plotly redraw w/ 2D histogram
		traces: [
	    {
	      x: csvReader.getColumn(chartState.x),
	      type: 'histogram',
	    }
	  ],
    layout: {
      xaxis: {
        title: chartState.x,
      },
    },
	};
}
